.contact_modal .formData[data-error]::after {
  content: attr(data-error);
  font-size: 18px;
  font-weight: 700;
  color: #54251A;
  display: block;
  margin: 5px 0px;
  text-align: left;
  opacity: 0;
  transition: 0.3s;
