  @media screen and (min-width: map-get($map: $breakpoints, $key:desktop)) {
    @content;
  }
}

@mixin larger-desktop {
  @media screen and (min-width: map-get($map: $breakpoints, $key:larger-desktop)) {
    @content;
  }
}

