sources.main.scss:
@import "../utils/variables.scss";
@import "../utils/mixins.scss";

body {
    @include desktop {
        max-width: 1400px;
        margin: auto;
    }
}
main {
    @include desktop {
      display: flex;
      justify-content: center;
      max-width: 1400px ;
      margin: auto;
    }
  .main_title {
    font-size: 14px;
    position: absolute;
    top: 2rem;
    right: 2rem;
    color: $color-primary;
    @include desktop {
        font-size: 36px;
        margin: auto;

    }
  }
  .photographers {
    display: flex;
    flex-direction: column;
    align-items: center;
    @include desktop {
      flex-direction: row;
      flex-wrap: wrap;
      justify-content: center;
    }
    article {
        @include desktop {
            margin: 3rem;
            width: 21rem;
        }
    }
  }
}
